    }
    const handleSubmit = (event)=>{
        event.prevent.default;
        onCreate(title);
    }
    return(
        <div> 
            <form>
                <label> Title</label>
                <input onChange={handleChange} value ={title}></input>
                <button>add book</button>
            </form>
        </div>
    )
};

